import React, { Component } from "react";
import pokemons from "../data";

class Pokemon extends Component {
  render() {
    return (
        <>
            <h2>{`Nome: ${pokemons[0].name} | `}</h2>
            <span>{`Tipo: ${pokemons[0].type} | `}</span>
            <span>{`Peso: ${pokemons[0].averageWeight.value}${pokemons[0].averageWeight.measurementUnit}`}</span>
        </>
    )
  }
}

export default Pokemon
